// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Attendance {
    // Structure to store attendance details
    struct AttendanceRecord {
        bool isPresent; // Whether the student is present
        uint256 timestamp; // The time of attendance
    }

    // Mapping to store attendance by student ID and class ID
    mapping(string => mapping(string => AttendanceRecord)) public attendance;

    // Event to log attendance marking
    event AttendanceMarked(string studentId, string classId, bool isPresent, uint256 timestamp);

    // Function to mark attendance
    function markAttendance(string memory studentId, string memory classId) public {
        require(bytes(studentId).length > 0, "Student ID is required.");
        require(bytes(classId).length > 0, "Class ID is required.");

        // Mark the attendance
        attendance[studentId][classId] = AttendanceRecord(true, block.timestamp);

        // Emit the event
        emit AttendanceMarked(studentId, classId, true, block.timestamp);
    }

    // Function to get attendance record
    function getAttendance(string memory studentId, string memory classId)
        public
        view
        returns (bool isPresent, uint256 timestamp)
    {
        AttendanceRecord memory record = attendance[studentId][classId];
        return (record.isPresent, record.timestamp);
    }
}
